mapper.select:
SELECT count(*)
FROM member
WHERE email=#{email}
AND pass =#{pass}
AND (state ='2' OR state ='3')
AND personDF='D_PE01'

SELECT memNO
FROM member
WHERE email = #{email}

SELECT memNO, personDF, email, pass, name, nickName, birth, sex, tel, joinDate, latestDate, state, id
FROM member NATURAL JOIN NP_MemMes
WHERE memNO = #{memNO}

mapper.update:
UPDATE member
SET pass = #{pass}, name = #{name}, nickName = #{nickName}, tel = #{tel}
WHERE memNO = #{memNO}

mapper.select:
SELECT count(*)
FROM member
WHERE memNO = #{memNO}
      AND pass = #{pass}

mapper.update:
UPDATE member
SET state = '4'
WHERE memNO = #{memNO}
      AND pass = #{pass}
